@namespace url(http://www.w3.org/1999/xhtml);
body {
        font-family: "Helvetica Neue", Helvetica, Arial, sans-serif !important;
        color: #ddd;
        background-color: #222 !important;
    }

    a,
    .tocnumber {
        color: #608dff !important;
        text-decoration: none;
    }

    a:hover {
        color: #637ab5 !important;
        text-decoration: underline;
    }

    h1 {
        border: 0px !important;
        font-family: 'Arial' !important;
        font-weight: 700 !important;
        font-size: 36px !important;
        color: #eee !important;
    }

    h2 {
        border: 0px !important;
        font-family: 'Arial' !important;
        font-weight: 400 !important;
        font-size: 26px !important;
        color: #eee !important;
    }

    h3 {
        border: 0px !important;
        font-family: 'Arial' !important;
        font-weight: 400 !important;
        font-size: 16px !important;
        color: #ddd !important;
    }

    h4 {
        color: #ccc !important;
    }

    [style*="color: black"] {
        color: white !important;
    }

    [style*="background:#f8f9fa"] {
        background: #2d2c2b !important;
        border: none !important;
    }

    .reflist,
    .refbegin {
        background-color: #282828 !important;
    }

    .thumbcaption {
        color: #888 !important;
    }

    #content h3 {
        font-size: 20px !important;
    }

    p {
        font-weight: 300 !important;
        line-height: 1.9 !important;
        color: #fff !important;
    }

    .portal-column-right span,
    .portal-column-left span,
    body.ns-100 .mw-body-content h2,
    .mw_metadata th,
    .tracklist th,
    #mp-tfl-h2 {
        color: #ddd !important;
        background-color: #444 !important;
    }

    body.ns-100 .mw-body-content th,
    body.ns-100 .mw-body-content div,
    body.ns-100 #portal td,
    .mw_metadata td,
    .tracklist td,
    .tracklist div {
        color: #ddd !important;
        border-width: 0px !important;
        background-color: #333 !important;
    }

    .portal-column-right,
    .portal-column-left,
    body.ns-100 .mw-body-content .subpages {
        color: #ddd !important;
        background-color: #222 !important;
        border-width: 0px !important;
    }

    .mw-highlight {
        background-color: #222 !important;
    }

    .mw-highlight .nc {
        color: #068dff;
    }

    .mw-highlight .nf {
        color: #068dff;
    }

    .mw-notification {
        background-color: #333 !important;
    }

    #Example {
        color: #ddd !important;
    }

    table,
    td,
    th {
        border: 0px !important;
        border-collapse: collapse !important;
        padding: 5px !important;
        /* color: #333 !important; */

        background-color: #444 !important;
        /* With this feature enabled, you loose visibility on colored table cells */
    }

    ul,
    li,
    caption {
        background: transparent !important;
        border: 0px !important;
        line-height: 1.8 !important;
        font-size: 14px !important;
        font-weight: 300 !important;
    }

    li span {
        background: transparent !important;
        border: 0px !important;
        font-size: 14px !important;
    }

    .referencetooltip {
        background-color: #282828 !important;
    }

    dl {
        font-weight: 300 !important;
    }

    dt {
        font-style: italic;
        font-size: 16px !important;
        margin-top: 10px !important;
    }

    dd {
        font-size: 15px !important;
    }

    #p-logo a {
        background-image: url('https://i.imgur.com/lNORoSn.png') !important;
        background-color: #222 !important;
    }

    .mw-wiki-logo {
        padding-left: 15px !important;
        padding-top: 10px !important;
        border: none !important;
    }

    code {
        padding: 3px !important;
        background: transparent !important;
        color: #39892f !important;
        border: 1px solid #555 !important;
    }

    .hatnote span {
        color: #888 !important;
        background-color: #222 !important;
    }

    tt {
        font-family: 'Source Code Pro', 'Lucida Sans Unicode', 'Courier New' !important;
        font-size: 16px !important;
    }

    .new,
    .new:link,
    .new:visited {
        color: #cd5b45 !important;
    }

    blockquote {
        border: 0px !important;
        font-style: italic !important;
        font-size: 20px !important;
        margin: 35px 75px !important;
        line-height: 1.5 !important;
        font-weight: 300 !important;
        background: transparent !important;
    }

    select {
        padding: 0px 10px;
    }

    .mw-ui-button,
    .mw-ui-progressive {
        border: 1px solid #888 !important;
        color: #347Bff !important;
        background: #222 !important;
        text-shadow: 0px 0px 0px;
    }

    .mw-ui-button.mw-ui-progressive:hover,
    .mw-ui-button.mw-ui-progressive:focus,
    .mw-ui-button.mw-ui-progressive:active,
    .mw-ui-button:hover,
    .mw-ui-progressive:hover,
    .mw-ui-button:focus,
    .mw-ui-progressive:focus {
        box-shadow: 0px 0px 0px !important;
        text-shadow: 0px 0px 0px !important;
        border: 1px solid #347Bff !important;
    }

    /* Media Viewer, image preview */

    .mw-mmv-above-fold  {
        background-color: #333 !important;
    }

    .mw-mmv-image-metadata {
        background-color: #111 !important;
    }

    .mw-mmv-dialog,
    .mw-mmv-dialog-down-arrow,
    .mw-mmv-download-attribution,
    .mw-mmv-download-pane,
    .mw-mmv-download-pane :hover {
        background-color: #333 !important;
    }

    /* End */

    #wpSave,
    #wpPreview,
    #wpDiff {
        border-radius: 4px !important;
        border: 1px solid #347Bff !important;
        color: #347Bff !important;
        background: #222 !important;
        text-shadow: 0px 0px 0px;
        padding: 0.5em 1em;
        cursor: pointer;
    }

    #wpSave:hover,
    #wpPreview:hover,
    #wpDiff:hover {
        color: rgba(52, 123, 255, 0.5) !important;
    }

    /* Search */

    #simpleSearch {
        position: relative;
        overflow: hidden;
        background: transparent !important;
        border-color: #666 !important;
    }

    #searchInput {
        height: 100%;
        color: #ddd !important;
        background: transparent !important;
    }
    
    /* Advanced search */
    
    input#ooui-1.oo-ui-inputWidget-input {
        color: #ddd !important;
        background: transparent !important;
    }
    
    #mw-searchoptions,
    #searchText {
        color: #ddd !important;
        background-color: #444 !important;
    }

    .mw-ui-vform .warning {
        background-color: #664a00 !important;
