.feed-follow-nav {
position: fixed;
width: 100%;
background-color: #1a2a33;
z-index: 2;
box-shadow: 0 2px 5px rgba(0,0,0,.15);
padding: 0 32px;
}
.feed-shared-follow-recommendation-card {
align-items: stretch;
flex-basis: 20%;
flex-direction: column;
min-height: 250px;
margin-top: 12px;
padding: 0;
background-color: rgba(0,0,0,.19);
}
.me-wvmp-viewer-card {
flex-direction: column;
align-items: center;
background-color: rgba(0,0,0,0);
cursor: pointer;
margin: 0;
padding: 16px 24px;
position: relative;
min-height: 280px;
max-height: 300px;
border-bottom: 1px solid rgba(0,0,0,.15);
border-right: 1px solid rgba(0,0,0,.15);
